mapper.select:
select no
    from tb_account
    where    id = #{id}
      and  name = #{name}
      and email = #{email}

mapper.update:
update tb_account
  set pass = #{pass}
   where no = #{no}

update tb_account
  set email = #{email}
      name  = #{name}
   where id = #{id}

mapper.select:
select id
    from tb_account
    where name = #{name}
      and email = #{email}

select count(id)
    from tb_account
    where id = #{id}
